all.map:
function (doc) {
  if (doc.form === "settings") {
    emit(doc._id, doc);
    emit([doc._id, "DE-de"], doc["DE-de"]);
    emit([doc._id, "EN-us"], doc["EN-us"]);
    emit([doc._id, "EN-en"], doc["EN-us"]);
  }
}

allByLang.map:
function (doc) {
  if (doc.form === "settings") {
    emit(["DE-de", doc._id], doc["DE-de"]);
    emit(["EN-us", doc._id], doc["EN-us"]);
    emit(["EN-en", doc._id], doc["EN-us"]);
  }
}
